#include <string.h>
#include <stdio.h>
size_t ft_strlen(char *s);

char assert(char *s)
{
	if (ft_strlen(s) != strlen(s)
		&& printf("\033[0;31mTest failed for input : \033[0m\"%s\"\n", s))
		return (1);
	printf("\033[1;32mOK\033[0m\n");
	return (0);
}

int main()
{
	assert("WEEKEND CHALLENGE !");
	assert("");
	assert("19");
	assert("");
	assert("WOW NICE STRLEN\n");
	assert("\ttabbb\nok ok");
	assert("");
}
